mapper.select:
select count(1) from sms_log where service_type = #{serviceType,jdbcType=INTEGER} and service_conditions = #{serviceConditions,jdbcType=VARCHAR} and send_status = 'SUCCESS'
and date_format(create_time,'%Y-%m-%d') >= date_format(#{start},'%Y-%m-%d') and date_format(create_time,'%Y-%m-%d') <= date_format(#{end},'%Y-%m-%d')
and mobile = #{mobile,jdbcType=BIGINT}

  	select 
	count(1) 
	from 
	sms_log t 
	where 
	t.create_time >= DATE_ADD(now(),INTERVAL "-1" MINUTE ) 
	and t.service_type = #{serviceType}
	and t.mobile = #{mobile}
	and t.service_conditions = #{serviceConditions}
